      it "assigns the requested confirmation as @confirmation" do
        confirmation = Confirmation.create! valid_attributes
        put :update, :id => confirmation.id, :confirmation => valid_attributes
        assigns(:confirmation).should eq(confirmation)
      end

      it "redirects to the confirmation" do
        confirmation = Confirmation.create! valid_attributes
        put :update, :id => confirmation.id, :confirmation => valid_attributes
        response.should redirect_to(confirmation)
