    end

    describe "with invalid params" do
      it "assigns the confirmation as @confirmation" do
        confirmation = Confirmation.create! valid_attributes
        # Trigger the behavior that occurs when invalid params are submitted
        Confirmation.any_instance.stub(:save).and_return(false)
        put :update, :id => confirmation.id.to_s, :confirmation => {}
        assigns(:confirmation).should eq(confirmation)
      end

      it "re-renders the 'edit' template" do
        confirmation = Confirmation.create! valid_attributes
